function AddBattle(name, runs)
  local battle = {}
  battle.Runs = runs
  Data.Battles[name] = battle
end

function InitializeCombatProfiles()
  echo("\nInitializing Combat Profiles...")
  Data.CombatProfiles = {}

  local claudius = {
    bodies = ConsumeHearts,
    heal = SerpentHeal,
    start = "bar",
    beforeBattle = SerpentBeforeBattle,
    beforeFight = SerpentBeforeFight,
    battleCompleted = SerpentBattleCompleted,
  }

  local ildon = {
    bodies = AbsorbCorpses
  }

  local engarlon = {
    bodies = Pyre
  }

  AddCombatProfile("claudius", claudius)
  AddCombatProfile("ildon", ildon)
  AddCombatProfile("engarlon", engarlon)
end

function AddCombatProfile(name, combatProfile)
  Data.CombatProfiles[name] = combatProfile
end

function InitializeTravel() echo("\nInitializing Travel...") InitializeLocations() InitializeRoutes()
for i = 0, Data.LocationCount - 1 do for j = 0, Data.LocationCount - 1 do for k = 0, Data.LocationCount - 1 do local jk = GetValue(j, k) local ji = GetValue(j, i) local ik = GetValue(i, k)
if (ji.Depth + ik.Depth < jk.Depth) then jk.Depth = ji.Depth + ik.Depth jk.Next = ji.Next SetValue(j, k, jk) end end end end end

function SerpentBeforeFight()
  send("warstyle "..GetStyle())
end

function SerpentBattleCompleted()
  DoTopup()
end

function Snapshot(callback)
  GetTicks(function(ticks)
    OnGotTicks(callback, ticks)
  end)
end

function OnGotTicks(callback, ticks)
  GetLevel(function(level)
    OnGotLevel(callback, ticks, level)
  end)
end

function OnGotLevel(callback, ticks, level)
  local totalTicks = ticks + level * 46
  NetWorth(function(netWorth)
    OnGotNetWorth(callback, totalTicks, netWorth)
  end)
end

function OnGotNetWorth(callback, totalTicks, netWorth)
  callback(totalTicks, netWorth, os.time())
end

function DoTopup()
  send("do 4 buy 6, 7 buy 5, 2 buy 4")
end
